package javaprogramsforpractice;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// Assignment =   +=   -=   *=   /=   %=
		//example for += and -=
		int a = 8;
		 //a = a+7; // Output 15
		a+=7;//15
		 System.out.println(a);
		 
		 a-=8;
		 System.out.println(a);
		
        //example for *= and /=
		 
		 int b = 25;
		// b = b*4;o/p 100
		 b*=4; // multiplies b with 4 and gives 100  b= b*4;
		 System.out.println(b);//100
		  
		 b/=5; //Divide b(100) with 5 and gives 20 b=b/4
		 System.out.println(b);//20
		 
		 //example for %=
		 
		 int d = 10;
		 d%=2; //d=d%2 10%2  remainder is 0
		 System.out.println(d);
		 
	
		 
	}

}
